    const top = screenH - clickY > rootH;
    const bottom = !top;

    if (right) {
      this.root.style.left = "".concat(clickX + 5, "px");
    }

    if (left) {
      this.root.style.left = "".concat(clickX - rootW - 5, "px");
    }

